|		ПО Итог.DR_Документ = ДР_ДатаПоставки.Ссылка
|			И (ДР_ДатаПоставки.Реквизит = ""ДатаПоставки"")
|		ЛЕВОЕ СОЕДИНЕНИЕ Документ.DR_Документ.ДополнительныеРеквизиты КАК ДР_NUMBER
