	СтрокиНастроекПоОтбору = СценарийСозданияXMLУКД.Строки.НайтиСтроки(Новый Структура("Использовать, Кнопка", Истина, "КорректировкаРеализации"));

	Если СтрокиНастроекПоОтбору.Количество() Тогда
		//<Используется первый найденный шаблон с признаком "Использовать">
		СтруктураФайлаДанных = СтрокиНастроекПоОтбору[0];

	КонецЕсли;

	Если СтруктураФайлаДанных <> Неопределено Тогда					

		ГибкиеНастройки = СтрЗаменить(СтруктураФайлаДанных.Служебные, "Источник", "МассивУКДКорРТУ"); 

