    СтрокаНоменклатур.ORDEREDQUANTITY 	= Формат(СтрокаНоменклатур.Количество, "ЧДЦ=3; ЧРД=.; ЧН=; ЧГ=0");
    СтрокаНоменклатур.VAT 				= Формат(СтрокаНоменклатур.СтавкаНДС, "ЧДЦ=; ЧГ=0");

